import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:8000';

function BookAppointment() {
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [slots, setSlots] = useState([]);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token'); // patient token

  // Fetch doctors (simple version: get all from /doctors endpoint or hardcode for now)
  useEffect(() => {
    async function fetchDoctors() {
      try {
        // You may need to implement this endpoint or adjust as needed
        const res = await axios.get(`${API_URL}/doctors`);
        setDoctors(res.data);
      } catch (err) {
        setDoctors([]);
      }
    }
    fetchDoctors();
  }, []);

  // Fetch slots when doctor changes
  useEffect(() => {
    async function fetchSlots() {
      if (!selectedDoctor) return;
      try {
        const res = await axios.get(`${API_URL}/doctor/${selectedDoctor}/available-slots`);
        setSlots(res.data);
      } catch (err) {
        setSlots([]);
      }
    }
    fetchSlots();
  }, [selectedDoctor]);

  const handleBook = async (slot) => {
    setMessage('');
    try {
      await axios.post(
        `${API_URL}/appointments/book`,
        {
          doctor_id: selectedDoctor,
          date: slot.date,
          start_time: slot.start_time,
          end_time: slot.end_time
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      setMessage('Appointment booked!');
      // Optionally refresh slots
      setSlots(slots.filter(s => !(s.date === slot.date && s.start_time === slot.start_time)));
    } catch (err) {
      setMessage(err.response?.data?.detail || 'Booking failed');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Book Appointment</h2>
      <div className="mb-3">
        <label>Select Doctor:</label>
        <select
          className="form-select"
          value={selectedDoctor}
          onChange={e => setSelectedDoctor(e.target.value)}
        >
          <option value="">-- Select --</option>
          {doctors.map(doc => (
            <option key={doc.id} value={doc.id}>{doc.full_name} ({doc.specialization})</option>
          ))}
        </select>
      </div>
      {slots.length > 0 && (
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Date</th>
              <th>Start Time</th>
              <th>End Time</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {slots.map((slot, idx) => (
              <tr key={idx}>
                <td>{slot.date}</td>
                <td>{slot.start_time}</td>
                <td>{slot.end_time}</td>
                <td>
                  <button className="btn btn-success" onClick={() => handleBook(slot)}>
                    Book
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      {message && <div className="alert alert-info mt-3">{message}</div>}
    </div>
  );
}

export default BookAppointment;
