import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:8000';

function DoctorAppointments() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('doctor_token');

  useEffect(() => {
    async function fetchAppointments() {
      setLoading(true);
      setMessage('');
      try {
        const res = await axios.get(`${API_URL}/doctor/appointments`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAppointments(res.data);
        if (res.data.length === 0) setMessage('No appointments found.');
      } catch (err) {
        setMessage('Failed to load appointments.');
      } finally {
        setLoading(false);
      }
    }
    if (token) fetchAppointments();
    else setMessage('Please log in as a doctor to view appointments.');
  }, [token]);

  return (
    <div className="container mt-4">
      <h2>My Appointments</h2>
      {loading && <div>Loading...</div>}
      {message && <div className="alert alert-info mt-3">{message}</div>}
      {appointments.length > 0 && (
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Start Time</th>
              <th>End Time</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appt) => (
              <tr key={appt.id}>
                <td>{new Date(appt.start_time).toLocaleString()}</td>
                <td>{new Date(appt.end_time).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default DoctorAppointments;
