import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const ChooseRole = () => {
  const navigate = useNavigate();
  const location = useLocation();
  // Determine if this is for register or login
  const isRegister = location.pathname.includes('register');

  const handleChoice = (role) => {
    if (isRegister) {
      if (role === 'patient') navigate('/register');
      else navigate('/doctor/register');
    } else {
      if (role === 'patient') navigate('/login');
      else navigate('/doctor/login');
    }
  };

  return (
    <div className="container mt-5" style={{maxWidth: 400}}>
      <h2 className="mb-4">{isRegister ? 'Register' : 'Login'} as</h2>
      <div className="d-grid gap-3">
        <button className="btn btn-outline-primary" onClick={() => handleChoice('patient')}>Patient</button>
        <button className="btn btn-outline-success" onClick={() => handleChoice('doctor')}>Doctor</button>
      </div>
    </div>
  );
};

export default ChooseRole;
