import React, { useState } from 'react';
import { registerUser } from '../api/auth';

function Register() {
  const [form, setForm] = useState({ full_name: '', email: '', password: '', role: 'patient', clinic_address: '', phone_number: '' });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    try {
      // Only send address/phone if doctor
      const submitForm = { ...form };
      if (form.role !== 'doctor') {
        delete submitForm.clinic_address;
        delete submitForm.phone_number;
      }
      await registerUser(submitForm);
      setMessage('Registration successful! Please login.');
    } catch (err) {
      setError(err.response?.data?.detail || 'Registration failed');
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input name="full_name" type="text" placeholder="Full Name" value={form.full_name} onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
        <input name="password" type="password" placeholder="Password" value={form.password} onChange={handleChange} required />
        <select name="role" value={form.role} onChange={handleChange} required>
          <option value="patient">Patient</option>
          <option value="doctor">Doctor</option>
        </select>
        {form.role === 'doctor' && (
          <>
            <input name="clinic_address" type="text" placeholder="Clinic Address" value={form.clinic_address} onChange={handleChange} required />
            <input name="phone_number" type="text" placeholder="Phone Number" value={form.phone_number} onChange={handleChange} required />
          </>
        )}
        <button type="submit">Register</button>
      </form>
      {message && <div style={{ color: 'green' }}>{message}</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
    </div>
  );
}

export default Register;
